<template>
  <div class="robot">
    <!-- 导航栏 -->
    <van-nav-bar class="loginBar" fixed @click-left="$router.back()">
      <div class="title" left-arrow slot="title">小智同学</div>
      <van-icon name="arrow-left" slot="left" />
    </van-nav-bar>
    <!-- 聊天区域 -->
    <div class="chat" ref="chat" >
      <div
        ref="item"
        class="item"
        :class="item.flag == 'user'?'userD':''"
        v-for="(item , index) in msgArr"
        :key="index"
      >
        <img
          :src="item.flag =='robot'?'data:image/jpeg;base64,':$store.state.photo"
          alt
        />
        <span :class="item.flag == 'user'? 'user':'robot'">{{item.msg}}</span>
      </div>
      <!-- <div class="item ">
          <div class="robot">
          <img src="../assets/Snipaste_2019-12-11_20-01-00.png" alt="">
          <span>你好,我是小智机器人</span>
          </div>
      </div>-->
    </div>
    <!-- 输入框 -->
    <div class="wirte">
      <van-search class="comment" v-model="value" show-action shape="round" @search="onComment">
        <div class="fabiao" slot="action" @click="onComment">发表</div>
      </van-search>
    </div>
  </div>
</template>

<script>
// 导入socket.io
import io from "socket.io-client";
export default {
  data() {
    return {
      value: "", //输入框内容
      socket: null,
      msgArr: [
        {
          flag: "robot",
          msg: "你好呀,我是小智"
        }
      ] //数据源
    };
  },
  methods: {
    // 点击发送按钮
    onComment() {
      // 发送信息
      this.socket.emit("message", {
        msg: this.value,
        timestamp: Date.now()
      });
      // 间输入框的信息保存到数据源中
      this.msgArr.push({
        flag: "user",
        msg: this.value
      });
      // 将输入框清空
      // this.value = "";
      window.console.log(this.$store.state);
    }
  },
  created() {
    //data数据创建了,但是el挂载点还没有创建(准备创建el)
    // 连接服务器
    this.socket = io(
      `http://ttapi.research.itcast.cn?token=${this.$store.state.user.token}`
    );
    // 接受信息
    this.socket.on("message", msg => {
      window.console.log(msg);
      // 将服务器返回的信息保存到数据源中
      this.msgArr.push({
        flag: "robot",
        msg: msg.msg
      });
    });
    
  },
  updated() {
  //data数据 第一次 渲染完毕 (完成初始渲染)
  // 自动滚动到底部
    // window.console.log(this.$refs.chat);
    this.$refs.chat.scrollTo(0,1000);
    
  },
};
</script>

